package strings;

import annotation.Platform;
import annotation.Quality;
import annotation.Site;
import annotation.Stage;
import org.junit.jupiter.api.Test;

/**
 * @author g1patil
 * 266. Palindrome Permutation
 */
@Quality(Stage.TESTED)
@Platform(Site.LEETCODE)
public class PalindromePermutation {

    public boolean canPermutePalindrome(String s) {
        if (s.isBlank())
            return false;

        int[] frequencyArray = new int[26];
        boolean foundMiddleChar = false;

        if( s.isBlank())
            return false;

        for(char c : s.toCharArray()){
            frequencyArray[c - 'a']++;
        }

        for(int frequency : frequencyArray){
            if(frequency % 2 == 1 && !foundMiddleChar){
                foundMiddleChar = true;
            } else if(frequency % 2 == 1 &&  foundMiddleChar){
                return false;
            }

        }

        return true;

    }

    @Test
    public void test_(){
        System.out.println(canPermutePalindrome("aab"));
    }

    @Test
    public void test_2(){
        System.out.println(canPermutePalindrome("ab"));
    }

    @Test
    public void test_3(){
        System.out.println(canPermutePalindrome("a"));
    }

    @Test
    public void test_4(){
        System.out.println(canPermutePalindrome(""));
    }
}
